pragma solidity ^0.8.20;
import {INameWrapper} from "@ensdomains/ens-contracts/contracts/wrapper/INameWrapper.sol";
import {IERC1155Receiver} from "@openzeppelin/contracts/interfaces/IERC1155Receiver.sol";
import {IERC721Receiver} from "@openzeppelin/contracts/interfaces/IERC721Receiver.sol";
import {IERC165} from "@openzeppelin/contracts/utils/introspection/IERC165.sol";

interface IENSResolver {
    /**
     * Sets the text data associated with an ENS node and key.
     * May only be called by the owner of that node in the ENS registry.
     * @param node The node to update.
     * @param key The key to set.
     * @param value The text data value to set.
     */
    function setText(
        bytes32 node,
        string calldata key,
        string calldata value
    ) external;

    /**
     * Sets the address associated with an ENS node.
     * May only be called by the owner of that node in the ENS registry.
     * @param node The node to update.
     * @param a The address to set.
     */
    function setAddr(bytes32 node, address a) external;

    /**
     * Returns the text data associated with an ENS node and key.
     * @param node The ENS node to query.
     * @param key The text data key to query.
     * @return The associated text data.
     */
    function text(
        bytes32 node,
        string calldata key
    ) external view returns (string memory);
}","keccak256":"0x0407924ae5a47e7dcaea13bdd091a402bdcfb905c61849fec02e99c04692e729","license":"MIT"},"contracts/libraries/Core.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import {ENS} from "./ENS.sol";

import {Gated, IGated} from "./Gated.sol";

import {Tableland} from "./Tableland.sol";

/**
 * @title Core
 * @dev Interface for the Ens system to support a decentralized Namespace of Database spaces
 * IPNS and Push protocol for code and space discussions
 * Tableland SQL in solidity for the databases and subspaces
 */

abstract contract Core is ENS, Gated, Tableland {
    enum Types {
        NULL,
        PAID_PRIVATE_INSTANCE,
        OPEN_PRIVATE_INSTANCE,
        PAID_INSTANCE,
        OPEN_INSTANCE,
        SUBNODE,
        CODE
    }

    struct SpaceInstance {
        address gatedContract;
        uint256 price;
        address creator;
    }

    mapping(bytes32 => SpaceInstance) public instances;

    mapping(bytes32 => address) public codeOwner;

    mapping(bytes32 => Types) public isType;

    error NoCodeOwner();

    error InstanceAlreadyExists();

    constructor(
        address _nameWrapper,
        address _publicResolver,
        address _gatedImplementation,
        address _subscriptionImplementation
    )
        ENS(_nameWrapper, _publicResolver)
        Gated(_gatedImplementation, _subscriptionImplementation)
        Tableland()
    {}

    /**
     * @dev createInstanceType
     * @param _newDBInstance The new instance
     * @param _gatedContract The gatedContract of the new instance
     * @param _price The price of the new instance
     */
    function createInstanceType(
        bytes32 _newDBInstance,
        address _gatedContract,
        uint _price
    ) internal {
        bool _isPrivate = _gatedContract != address(0);
        if (!_isPrivate && _price > 0) {
            isType[_newDBInstance] = Types.PAID_INSTANCE;
            createSubscription(_price, _newDBInstance);
        } else if (_isPrivate && _price > 0) {
            isType[_newDBInstance] = Types.PAID_PRIVATE_INSTANCE;
            createSubscription(_price, _newDBInstance);
        } else if (_isPrivate) {
            isType[_newDBInstance] = Types.OPEN_PRIVATE_INSTANCE;
        } else {
            isType[_newDBInstance] = Types.OPEN_INSTANCE;
        }
    }

    /**
     * @dev Check if the sender has access to the given instance
     * @param _instance The instance to check
     * @param _sender The sender to check
     * @return bool
     */
    function hasViewAccess(
        bytes32 _instance,
        address _sender
    ) public view returns (bool) {
        address _gatedAddress = instances[_instance].gatedContract;
        if (isType[_instance] == Types.PAID_INSTANCE) {
            return hasActiveSubscription(_instance, _sender);
        } else if (isType[_instance] == Types.PAID_PRIVATE_INSTANCE) {
            return
                getAccess(_sender, _gatedAddress) ||
                hasActiveSubscription(_instance, _sender);
        } else if (
            isType[_instance] == Types.OPEN_PRIVATE_INSTANCE ||
            isType[_instance] == Types.OPEN_INSTANCE
        ) {
            return true;
        } else {
            return false;
        }
    }

    function hasMutateAccess(
        bytes32 _instance,
        address _sender
    ) public view returns (bool access) {
        address _gatedAddress = instances[_instance].gatedContract;
        Types _instanceType = isType[_instance];
        if (_instanceType == Types.PAID_INSTANCE) {
            access = instances[_instance].creator == _sender;
        } else if (_instanceType == Types.PAID_PRIVATE_INSTANCE) {
            access =
                getAccess(_sender, _gatedAddress) ||
                instances[_instance].creator == _sender;
        } else if (_instanceType == Types.OPEN_PRIVATE_INSTANCE) {
            access = getAccess(_sender, _gatedAddress);
        } else if (_instanceType == Types.OPEN_INSTANCE) {
            access = true;
        }
        uint8 _isType = uint8(_instanceType);
        if (_isType > 4 || _instanceType == Types.NULL) {
            access = false;
        }
    }

    function insertNewMembers(
        bytes32 _instance,
        address[] memory _members
    ) external {
        address _gatedAddress = instances[_instance].gatedContract;
        address _owner = instances[_instance].creator;
        require(_owner == msg.sender, "No access");
        if (_gatedAddress != address(0)) {
            IGated(_gatedAddress).mint(_members);
        }
        _insertMembers(_instance, _members);
    }

    function removeMembers(
        bytes32 _instance,
        address[] memory _members
    ) external {
        address _gatedAddress = instances[_instance].gatedContract;
        address _owner = instances[_instance].creator;
        require(_owner == msg.sender, "No access");
        if (_gatedAddress != address(0)) {
            IGated(_gatedAddress).burn(_members);
        }
        _removeMembers(_instance, _members);
    }
}
","keccak256":"0x1a0d52929a16bbebe1707914a6f0d1587a07b05eaa91eb7083352b94d0027f76","license":"MIT"},"contracts/libraries/ENS.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import {IERC165} from "@openzeppelin/contracts/utils/introspection/IERC165.sol";

import {IERC1155Receiver, IENSResolver, INameWrapper, IERC165} from "../interfaces/interfaces.sol";

/**
 * @title ENS
 * @dev Interface for the ENSsystem to support a decentralized Namespace of Database spaces
 * IPNS protocol for code
 * Tableland SQL in solidity for the databases and subspaces
 */

abstract contract ENS  {

    IENSResolver public immutable PUBLIC_RESOLVER;
    INameWrapper public immutable NAME_WRAPPER;

    bytes32 public BASE_NODE;
    uint256 public BASE_NODE_ID;
    uint256 public DOMAIN_ID;

    error NoInstanceAccess();
    error InvalidTokenAmount();
    error InvalidTokenSender();

    constructor(
        address _nameWrapper,
        address _publicResolver
    ) {
        NAME_WRAPPER = INameWrapper(_nameWrapper);
        PUBLIC_RESOLVER = IENSResolver(_publicResolver);
    }

    /*
     * @dev Function to create a new subnode.
     * @param {bytes32} node - Parent node.
     * @param {string} label - Subnode name label.
     * @return {bytes32} - New subnode.
     */

    function createSubNode(
        bytes32 node,
        string memory label
    ) internal returns (bytes32 newSubNode) {
        newSubNode = NAME_WRAPPER.setSubnodeRecord(
            // The node to create a subnode 
            node,
            // The label of the subnode
            label,
            // Owner
            address(this),
            // Resolver
            address(PUBLIC_RESOLVER),
            // TTL
            0,
            // Fuses
            0,
            // EXPIRY
            0
        );
    }


    function onERC1155Received(
        address,
        address /* from */,
        uint256 tokenId,
        uint256 value,
        bytes calldata
    ) external returns (bytes4) {
        if (msg.sender != address(NAME_WRAPPER)) {
            revert InvalidTokenSender();
        }
        if (value != 1) {
            revert InvalidTokenAmount();
        }

        if (BASE_NODE == 0) {
            BASE_NODE_ID = tokenId;
            BASE_NODE = bytes32(tokenId);
        }

        return IERC1155Receiver.onERC1155Received.selector;
    }

    function onERC1155BatchReceived(
        address,
        address,
        uint256[] calldata,
        uint256[] calldata,
        bytes calldata
    ) external pure returns (bytes4) {
        revert();
    }
}
","keccak256":"0x0241315aa0ff5ed380964abdd65533c71ec4313078bcc277dae5d21b11fad2ed","license":"MIT"},"contracts/libraries/Gated.sol":{"content":"// SPDX-License-Identifier: MIT

pragma solidity ^0.8.17;
import {Clones} from "@openzeppelin/contracts/proxy/Clones.sol";
import {IERC721} from "@openzeppelin/contracts/token/ERC721/IERC721.sol";
import {Address} from "@openzeppelin/contracts/utils/Address.sol";

interface IGated {
    function mint(address[] memory newMembers) external;

    function burn(address[] memory _members) external;

    function getRemainingTime(address owner) external view returns (uint256);

    function subscribe(address newMember) external;

    function extendSubscription(address member) external;

    function balanceOf(address owner) external view returns (uint256);
}

abstract contract Gated {
    address internal gatedImplementation;
    address internal subscriptionImplementation;
    uint256 internal constant MONTH = 30 days;

    struct SubscriptionStruct {
        IGated subscriptionContract;
        address creator;
        uint256 prize;
    }

    mapping(bytes32 => SubscriptionStruct) public instanceSubscription;

    // Constructor
    constructor(
        address _gatedImplementation,
        address _subscriptionImplementation
    ) {
        gatedImplementation = _gatedImplementation;
        subscriptionImplementation = _subscriptionImplementation;
    }

    function createSubscription(uint _keyPrice, bytes32 _instanceID) internal {
        instanceSubscription[_instanceID] = SubscriptionStruct({
            subscriptionContract: IGated(
                creatSubscriptionContract(_instanceID)
            ),
            creator: msg.sender,
            prize: _keyPrice
        });
    }

    // Function to create a new OptimisticResolver contract and associate it with a schema
    function createGatedContract(
        address[] memory _members,
        bytes32 salt
    ) internal returns (address accessControlClone) {
        // Create new resolver contract
        accessControlClone = Clones.cloneDeterministic(
            gatedImplementation,
            salt
        );

        (bool success, ) = accessControlClone.call(
            abi.encodeWithSignature("initialize(address[])", _members)
        );

        require(success, "error deploying");
    }

    // Function to create a new OptimisticResolver contract and associate it with a schema
    function creatSubscriptionContract(
        bytes32 salt
    ) internal returns (address accessControlClone) {
        // Create new resolver contract
        accessControlClone = Clones.cloneDeterministic(
            subscriptionImplementation,
            salt
        );

        (bool success, ) = accessControlClone.call(
            abi.encodeWithSignature("initialize(address)", address(this))
        );

        require(success, "error deploying");
    }

    function getDeterministicAddress(
        bytes32 _salt
    ) public view returns (address) {
        return Clones.predictDeterministicAddress(gatedImplementation, _salt);
    }

    function getSuscriptionDeterministicAddress(
        bytes32 _salt
    ) public view returns (address) {
        return
            Clones.predictDeterministicAddress(
                subscriptionImplementation,
                _salt
            );
    }

    function getAccess(
        address _sender,
        address _gatedContract
    ) public view returns (bool) {
        return
            _gatedContract == address(0)
                ? false
                : IERC721(_gatedContract).balanceOf(_sender) > 0;
    }

    function getRemainingSubscriptionTime(
        bytes32 _instanceID,
        address _owner
    ) public view returns (uint256) {
        return
            instanceSubscription[_instanceID]
                .subscriptionContract
                .getRemainingTime(_owner);
    }

    function hasActiveSubscription(
        bytes32 _instanceID,
        address _subscriber
    ) public view returns (bool) {
        return
            instanceSubscription[_instanceID].subscriptionContract.balanceOf(
                _subscriber
            ) > 0;
    }

    /**
     * @dev PurchaseSubscription function for an agentID
     * @param _instanceID to subscribe
     */
    function purchaseSubscription(bytes32 _instanceID) internal {
        uint256 _priceToPay = instanceSubscription[_instanceID].prize;
        require(
            _priceToPay == msg.value,
            "QUADB: No price set for this instance"
        );

        instanceSubscription[_instanceID].subscriptionContract.subscribe(
            msg.sender
        );

        Address.sendValue(
            payable(instanceSubscription[_instanceID].creator),
            (msg.value * 99) / 100
        );
    }

    function extendSubscription(bytes32 _instanceID) internal {
        uint256 _priceToPay = instanceSubscription[_instanceID].prize;

        require(
            _priceToPay == msg.value,
            "QUADB: No price set for this instance"
        );

        instanceSubscription[_instanceID]
            .subscriptionContract
            .extendSubscription(msg.sender);

        Address.sendValue(
            payable(instanceSubscription[_instanceID].creator),
            (msg.value * 99) / 100
        );
    }

    function getTime() public view returns (uint256) {
        return block.timestamp;
    }

    // Function to receive Ethers
    receive() external payable {}
}
